import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

const Counter = () => {
  const count = useSelector((state) => state.count);
  const dispatch = useDispatch();

  const increment = () => {
    dispatch({ type: 'INCREMENT' });
  };

  const decrement = () => {
    dispatch({ type: 'DECREMENT' });
  };

  const containerStyle = {
    backgroundColor: 'lightblue', // Cambia el color de fondo a celeste claro
    padding: '20px', // Añade un espacio en el interior del contenedor
    borderRadius: '8px', // Agrega bordes redondeados al contenedor
  };

  return (
    <div style={containerStyle}>
      <h2>Contador: {count}</h2>
      <button onClick={increment} style={{ color: 'pink' }}>Incrementar</button>
      <button onClick={decrement} style={{ color: 'red' }}>Decrementar</button>
    </div>
  );
};

export default Counter;
