// store.js
import { createStore } from 'redux';

// Definimos el reducer
const reducer = (state = { count: 0 }, action) => {
  switch (action.type) {
    case 'INCREMENT':
      return { count: state.count + 1 };
    case 'DECREMENT':
      return { count: state.count - 1 };
    default:
      return state;
  }
};

// Creamos la tienda de Redux
const store = createStore(reducer);

export default store;
